import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchStocks } from '../redux/actions';

function StockList() {
  const dispatch = useDispatch();
  const { stocks, loading, error } = useSelector((state) => state.stocks);

  useEffect(() => {
    dispatch(fetchStocks());
  }, [dispatch]);

  const calculateProfitLoss = (stock) => {
    const profitLoss = (stock.currentPrice - stock.buyPrice) * stock.quantity;
    return profitLoss.toFixed(2);
  };

  const calculateProfitLossPercent = (stock) => {
    const percent = ((stock.currentPrice - stock.buyPrice) / stock.buyPrice) * 100;
    return percent.toFixed(2);
  };

  if (loading) {
    return <div className="loading">Loading stocks...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="stock-list">
      <h2>My Portfolio</h2>
      {stocks.length === 0 ? (
        <p>No stocks in portfolio. Add your first stock!</p>
      ) : (
        <table className="stock-table">
          <thead>
            <tr>
              <th>Symbol</th>
              <th>Company</th>
              <th>Quantity</th>
              <th>Buy Price</th>
              <th>Current Price</th>
              <th>P/L</th>
              <th>P/L %</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {stocks.map((stock) => {
              const pl = calculateProfitLoss(stock);
              const plPercent = calculateProfitLossPercent(stock);
              return (
                <tr key={stock._id}>
                  <td><strong>{stock.symbol}</strong></td>
                  <td>{stock.companyName}</td>
                  <td>{stock.quantity}</td>
                  <td>${stock.buyPrice}</td>
                  <td>${stock.currentPrice}</td>
                  <td className={pl >= 0 ? 'positive' : 'negative'}>
                    ${pl}
                  </td>
                  <td className={plPercent >= 0 ? 'positive' : 'negative'}>
                    {plPercent}%
                  </td>
                  <td>
                    <Link to={`/stock/${stock._id}`}>View</Link>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default StockList;
